<?php/** * The template for displaying posts in the cwp_gallery post format. * * @package metro-creativex */?>			<article id="post-<?php the_ID(); ?>" <?php post_class(); ?>>				<?php                $cwp_posttitle = get_the_title();                $cwp_feat_image = wp_get_attachment_image_src( get_post_thumbnail_id( $post->ID ), 'single-post-thumbnail' );                if(isset($cwp_feat_image[0])):                    echo '<div class="img"><img src='.$cwp_feat_image[0].' alt="'.$cwp_posttitle.'"></div>';                endif;                ?>				<div class="post_icon" style="background-image:url(<?php echo get_template_directory_uri(); ?>/images/pt_gallery.png);"></div>				<div class="post_content">					<a href="<?php the_permalink(); ?>" title="<?php the_title(); ?>"><?php the_title(); ?></a>					<div class="post_date"><?php echo get_the_date('d M Y'); ?></div>				</div><!--/post_content-->			</article>