<?php/** * The Header for our theme. * * Displays all of the <head> section and everything up till <main id="main"> * * @package cwp */?><!DOCTYPE html><html <?php language_attributes(); ?>>	<head>		<meta charset="<?php bloginfo( 'charset' ); ?>" />		<title><?php wp_title('|', true, 'right'); ?></title>		<meta name="viewport" content="width=device-width, initial-scale=1.0">		<link rel="profile" href="http://gmpg.org/xfn/11">		<link rel="pingback" href="<?php bloginfo( 'pingback_url' ); ?>">		 		<?php wp_head(); ?>	</head>	<body <?php body_class(); ?>>		<header class="header">			<?php if(get_header_image()): ?>			<img src="<?php header_image(); ?>" height="<?php echo get_custom_header()->height; ?>" width="<?php echo get_custom_header()->width; ?>" alt="" />			<?php endif; ?>			<div id="logo">				<?php if ( get_theme_mod( 'codeinwp_logo' ) ) : ?>                    <div class='site-logo'>						<a href='<?php echo esc_url( home_url( '/' ) ); ?>' title='<?php echo esc_attr( get_bloginfo( 'name', 'display' ) ); ?>' rel='home'><img src='<?php echo esc_url( get_theme_mod( 'codeinwp_logo' )); ?>' alt='<?php echo esc_attr( get_bloginfo( 'name', 'display' ) ); ?>'></a>                    </div>                <?php else : ?>					<a href="<?php echo home_url(); ?>">						<img src="<?php echo get_template_directory_uri() ?>/images/logo.png" alt="">					</a>                <?php endif; ?>			</div><!-- /logo -->						<div class="openmenuresp"><?php _e('Menu','cwp'); ?></div>			<nav>				<?php 				  $terms = get_categories();				  if ($terms) {				    foreach( $terms as $term ) {				    	$post_nr = $term->count;				    	if ( $post_nr == "1" )				    		$post_nr_display = "article";				    	else {				    		$post_nr_display = 'articles';				    	}				  	  echo '				  	  	<a href="' . get_category_link( $term->term_id ) . '" class="color-code" title="' . $term->name.'">							' . $term->name.'							<div class="read bg-code"> 								<p>'.$post_nr.'</p><span>'.$post_nr_display.'</span>							</div>						</a>';						}				  }				?>					</nav>			<div class="navrespgradient"></div>			<div id="social">				<?php if ( get_theme_mod( 'codeinwp_social_link_fb' ) ) : ?>                    	<a href="<?php echo esc_url( get_theme_mod( 'codeinwp_social_link_fb' )); ?>"><img src="<?php echo get_template_directory_uri(); ?>/images/facebook.png" alt=""></a>                 <?php endif; ?>					            <?php if ( get_theme_mod( 'codeinwp_social_link_tw' ) ) : ?> 						<a href="<?php echo esc_url(  get_theme_mod( 'codeinwp_social_link_tw' )); ?>"><img src="<?php echo get_template_directory_uri(); ?>/images/twitter.png" alt=""></a>               <?php endif; ?>			</div><!-- /social -->	</header>		<div id="topside">			<div id="searchform">				<?php get_search_form(); ?>			</div><!--/searchform-->							<div class="pages">				<?php wp_nav_menu( array( 				'theme_location' => 'secound',				'menu_item_start' => 1,  				'menu_item_end' => 5 ) ); ?>			</div><!--/pages-->			<div class="clearfix"></div>			