<?php/** * The template for displaying posts in the Gallery post format. * * @package cwp */?>			<article id="post-<?php the_ID(); ?>" <?php post_class(); ?>>								<div class="img">					<div class="img_gallery">					<?php 					if ( get_post_gallery() ) :			            $gallery = get_post_gallery( get_the_ID(), false );			            /* Loop through all the image and output them one by one */			            foreach( $gallery['src'] AS $src ) { 			            	$image_link = str_replace("-150x150.",".", $src );			            	?>			                <img src="<?php echo $image_link; ?>" class="active"/>			            <?php } endif; 			        ?>			        </div><!--/img_gallery-->			       				</div><!--/img-->								<div class="post_icon" style="background-image:url(<?php echo get_template_directory_uri(); ?>/images/pt_gallery.png);"></div>				<div class="post_content">					<a href="<?php the_permalink(); ?>" title="<?php the_title(); ?>"><?php the_title(); ?></a>					<div class="post_date"><?php echo get_the_date('d M Y'); ?></div>				</div><!--/post_content-->			</article>