<?php/** * The template for displaying posts in the Image post format. * * @package cwp */?>			<article id="post-<?php the_ID(); ?>" <?php post_class(); ?>>				<?php 					$posttitle = get_the_title();					$feat_image = wp_get_attachment_image_src( get_post_thumbnail_id( $post->ID ), 'single-post-thumbnail' );					if(isset($feat_image[0])):						echo '<div class="img"><img src='.$feat_image[0].' alt="'.$posttitle.'"></div>';					endif;					?>				<div class="post_icon" style="background-image:url(<?php echo get_template_directory_uri(); ?>/images/pt_image.png);"></div>				<div class="post_content">					<a href="<?php the_permalink(); ?>"><?php the_title(); ?></a>					<div class="post_date"><?php echo date("d M Y"); ?></div>				</div><!--/post_content-->			</article>